// src/views/Home.js
import React, { useEffect, useState } from 'react';
import Song from '../components/Song';
import Header from '../components/Header';
import SearchBar from '../components/SearchBar';

const Home = () => {
  const [songs, setSongs] = useState([]);

  useEffect(() => {

    const songList = [
      { title: 'Mi version de ti', artist: 'DAAZ', duration: "3:59" },
      { title: 'Orion', artist: 'Boza, Elena Rose', duration: "4:02" },
      { title: 'Motel California', artist: 'Sabino', duration:  "3:35"},
      { title: 'Nubia', artist: 'Esperón', duration:  "3:42"}
    ];

    setSongs(songList);
    console.log('La app se ha cargado correctamente');
  }, []); // El array vacío asegura que se ejecute solo una vez, como componentDidMount

  return (
    <div className="home">
      <Header />
      <SearchBar />
      <div className="songs-container">
      {songs.map((song, index) => (
        <Song
        key={index}
        title={song.title}
        artist={song.artist}
        duration={song.duration}
        />
      ))}
      </div>
    </div>
  );
};

export default Home;
